async function onRequestPost(context) {
  try {
    const { message, persona, industry, conversationHistory = [] } = await context.request.json();
    const DEEPSEEK_API_KEY = context.env.DEEPSEEK_API_KEY;
    if (!DEEPSEEK_API_KEY) {
      return new Response(JSON.stringify({
        success: false,
        error: "\u672A\u914D\u7F6E API Key"
      }), {
        status: 500,
        headers: { "Content-Type": "application/json" }
      });
    }
    const conversationRound = conversationHistory.length;
    const systemPrompt = generateScenarioPrompt(industry, persona, conversationRound);
    const messages = [
      { role: "system", content: systemPrompt },
      ...conversationHistory.map((msg) => ({
        role: msg.role === "user" ? "user" : "assistant",
        content: msg.content
      })),
      { role: "user", content: message }
    ];
    const response = await fetch("https://dashscope.aliyuncs.com/compatible-mode/v1/chat/completions", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${DEEPSEEK_API_KEY}`
      },
      body: JSON.stringify({
        model: "deepseek-v3.2",
        messages,
        temperature: 0.7,
        max_tokens: 500,
        top_p: 0.9
      })
    });
    if (!response.ok) {
      const error = await response.text();
      console.error("DeepSeek API Error:", error);
      return new Response(JSON.stringify({
        success: false,
        error: `API \u8C03\u7528\u5931\u8D25: ${response.status}`
      }), {
        status: response.status,
        headers: { "Content-Type": "application/json" }
      });
    }
    const data = await response.json();
    return new Response(JSON.stringify({
      success: true,
      reply: data.choices[0].message.content,
      usage: data.usage
      // 返回 token 使用情况
    }), {
      headers: {
        "Content-Type": "application/json",
        "Access-Control-Allow-Origin": "*"
      }
    });
  } catch (error) {
    console.error("Function Error:", error);
    return new Response(JSON.stringify({
      success: false,
      error: error.message
    }), {
      status: 500,
      headers: { "Content-Type": "application/json" }
    });
  }
}
